import React, {createContext, ReactNode, useContext, useState} from "react";
import * as AuthSession from 'expo-auth-session';
import {
    REDIRECT_URL,
    SCOPE,
    RESPONSE_TYPE,
    CLIENT_ID,
    CDN_IMAGE
} from '../configs';
import {api} from "../services/api";

type User = {
    id: string,
    username: string,
    firstName: string,
    avatar: string,
    email: string,
    token: string
}

type AuthContexData = {
    user: User
}

type AuthProviderProps = {
    children: ReactNode,
}

export const AuthContext = createContext({} as AuthContexData);

function AuthProvider({children}: AuthProviderProps) {
    const [user, setUser] = useState<User>({} as User);
    const [loading, setLoading] = useState(false);

    function SignIn() {
        try {
            setLoading(true);
            const authUrl =  `${api.default.baseURL}/oauth2/authorize?client_id=889309135357751368&redirect_uri=https%3A%2F%2Fauth.expo.io%2Fapp-gameplay&response_type=code&scope=identify%20email%20connections%20guilds`;

            AuthSession
                .startAsync({authUrl})

        } catch (error) {
        }
    }

    return (
        <AuthContext.Provider value={{
            user
        }}>
            {children}
        </AuthContext.Provider>
    )
}

function useAuth() {
    const context = useContext(AuthContext);

    return context;
}

export {AuthProvider, useAuth}
